INSERT INTO places (region_id, name_ko, name_en, description_ko, description_en, longitude, latitude, image_url, address, contact_info, website_url, kakao_place_id, tour_api_place_id, opening_hours, themes, cost_info, created_at, updated_at)
VALUES
    (1, 'JYP 사옥', 'JYP Entertainment Building', 'JYP 엔터테인먼트의 본사 사옥으로 박진영이 설립한 대한민국 3대 기획사 중 하나입니다. 원더걸스, TWICE, Stray Kids 등 수많은 K-pop 스타들이 소속된 기획사의 본사로, 팬들에게는 성지와 같은 곳입니다.', 'The headquarters building of JYP Entertainment, one of Korea''s top 3 entertainment companies founded by Park Jin-young. Home to numerous K-pop stars including Wonder Girls, TWICE, and Stray Kids, this building serves as a pilgrimage site for fans worldwide.', 127.0276, 37.5413, 'https://example.com/jyp.jpg', '서울특별시 강동구 성내동', '02-3401-0114', 'https://www.jype.com', 'jyp_kakao_001', 'jyp_tour_001', '{"monday": "09:00-18:00", "tuesday": "09:00-18:00", "wednesday": "09:00-18:00", "thursday": "09:00-18:00", "friday": "09:00-18:00", "saturday": "closed", "sunday": "closed"}', '["K_POP"]', '무료 (외부 관람만 가능)', NOW(), NOW()),
    (1, 'SM ARTIST & MUSIC CENTER', 'SM ARTIST & Music CENTER', 'SM 엔터테인먼트의 아티스트 및 뮤직 센터로 한국 대표 엔터테인먼트 기업의 문화 복합 공간입니다. 소녀시대, EXO, aespa 등 글로벌 K-pop 스타들의 굿즈와 음반을 만나볼 수 있으며, 팬들을 위한 다양한 이벤트와 전시가 열리는 곳입니다.', 'SM Entertainment''s Artist & Music Center, a cultural complex space of Korea''s leading entertainment company. Visitors can explore merchandise and albums of global K-pop stars like Girls'' Generation, EXO, and aespa, with various events and exhibitions held for fans.', 127.0456, 37.5287, 'https://example.com/sm_center.jpg', '서울특별시 강남구 압구정로', '02-3438-0100', 'https://www.smtown.com', 'sm_center_kakao_002', 'sm_center_tour_002', '{"monday": "10:00-19:00", "tuesday": "10:00-19:00", "wednesday": "10:00-19:00", "thursday": "10:00-19:00", "friday": "10:00-19:00", "saturday": "10:00-18:00", "sunday": "10:00-18:00"}', '["K_POP"]', '무료 입장, 굿즈 구매 별도', NOW(), NOW()),
    (1, 'HYBE 사옥', 'HYBE Building', 'HYBE 엔터테인먼트 본사 사옥으로 글로벌 슈퍼스타 BTS가 소속된 기획사의 본사입니다. 방탄소년단을 비롯해 NewJeans, LE SSERAFIM 등 차세대 K-pop 아티스트들의 본거지로, 전 세계 팬들이 찾는 한류 성지입니다.', 'HYBE Entertainment headquarters building, home to global superstars BTS. As the base for BTS and next-generation K-pop artists like NewJeans and LE SSERAFIM, this building is a Hallyu pilgrimage site visited by fans worldwide.', 126.9579, 37.5665, 'https://example.com/hybe.jpg', '서울특별시 용산구 한강대로', '02-3444-0105', 'https://www.hybecorp.com', 'hybe_kakao_003', 'hybe_tour_003', '{"monday": "09:00-18:00", "tuesday": "09:00-18:00", "wednesday": "09:00-18:00", "thursday": "09:00-18:00", "friday": "09:00-18:00", "saturday": "closed", "sunday": "closed"}', '["K_POP"]', '무료 (외부 관람만 가능)', NOW(), NOW()),
    (1, '하이커 그라운드 종로점', 'Hi-Ker Ground Jongno', '케이팝 굿즈와 다양한 K-pop 문화 체험을 즐길 수 있는 복합 문화 공간입니다. 최신 아이돌 굿즈부터 포토카드까지 다양한 상품을 만나볼 수 있으며, 팬들끼리 소통하고 덕질을 즐길 수 있는 특별한 공간입니다.', 'A complex cultural space where visitors can enjoy K-pop goods and various K-pop cultural experiences. From the latest idol merchandise to photocards, fans can discover diverse products and enjoy a special space for communication and fan activities.', 126.9816, 37.5703, 'https://example.com/hiker_ground.jpg', '서울특별시 종로구 종로', '02-765-4321', 'https://www.hikerground.com', 'hiker_kakao_004', 'hiker_tour_004', '{"monday": "10:00-22:00", "tuesday": "10:00-22:00", "wednesday": "10:00-22:00", "thursday": "10:00-22:00", "friday": "10:00-23:00", "saturday": "10:00-23:00", "sunday": "10:00-22:00"}', '["K_POP"]', '입장료 5,000원, 체험비 별도', NOW(), NOW()),
    (1, '케이팝 스퀘어 홍대점', 'K-pop Square Hongdae', '홍대에 위치한 케이팝 전문 쇼핑몰로 젊은 문화의 중심지에서 만나는 K-pop 성지입니다. 다양한 아이돌 그룹의 굿즈와 음반을 한 곳에서 만날 수 있으며, 팬 이벤트와 사인회 등이 자주 열리는 핫플레이스입니다.', 'A K-pop specialized shopping mall located in Hongdae, serving as a K-pop sanctuary in the heart of youth culture. Visitors can find merchandise and albums from various idol groups in one place, and it''s a hotspot where fan events and autograph sessions are frequently held.', 126.9235, 37.5563, 'https://example.com/kpop_square.jpg', '서울특별시 마포구 홍익로', '02-334-5678', 'https://www.kpopsquare.com', 'kpop_square_kakao_005', 'kpop_square_tour_005', '{"monday": "11:00-23:00", "tuesday": "11:00-23:00", "wednesday": "11:00-23:00", "thursday": "11:00-23:00", "friday": "11:00-24:00", "saturday": "11:00-24:00", "sunday": "11:00-23:00"}', '["K_POP"]', '무료 입장', NOW(), NOW()),
    (1, '뮤직아트 명동 팔레트점', 'Music Art Myeongdong Palette', '명동 중심가에 위치한 케이팝 굿즈 전문점으로 한국을 대표하는 쇼핑 거리에서 만나는 K-pop 문화 공간입니다. 최신 앨범부터 한정판 굿즈까지 다양한 상품을 보유하고 있으며, 외국인 관광객들에게 인기가 높은 명소입니다.', 'A K-pop goods specialty store located in the heart of Myeongdong, offering K-pop culture in Korea''s representative shopping district. From the latest albums to limited edition merchandise, it houses diverse products and is a popular destination among foreign tourists.', 126.9849, 37.5636, 'https://example.com/musicart.jpg', '서울특별시 중구 명동길', '02-777-1234', 'https://www.musicart.co.kr', 'musicart_kakao_006', 'musicart_tour_006', '{"monday": "10:00-22:00", "tuesday": "10:00-22:00", "wednesday": "10:00-22:00", "thursday": "10:00-22:00", "friday": "10:00-22:30", "saturday": "10:00-22:30", "sunday": "10:00-22:00"}', '["K_POP"]', '무료 입장', NOW(), NOW()),
    (1, '포카부', 'Pokkabu', '케이팝 포토카드 전문 매장으로 덕후들의 성지라 불리는 특별한 공간입니다. 희귀 포토카드부터 일반 포카까지 다양한 컬렉션을 보유하고 있으며, 교환과 거래가 활발히 이루어지는 포카 덕후들의 메카입니다.', 'A K-pop photocard specialty store known as a sanctuary for collectors. Housing collections from rare photocards to general ones, it serves as a mecca for photocard enthusiasts where active trading and exchanges take place.', 126.9245, 37.5573, 'https://example.com/pokkabu.jpg', '서울특별시 마포구 와우산로', '02-456-7890', 'https://www.pokkabu.com', 'pokkabu_kakao_007', 'pokkabu_tour_007', '{"monday": "12:00-21:00", "tuesday": "12:00-21:00", "wednesday": "12:00-21:00", "thursday": "12:00-21:00", "friday": "12:00-22:00", "saturday": "11:00-22:00", "sunday": "12:00-21:00"}', '["K_POP"]', '무료 입장', NOW(), NOW()),
    (1, 'SM TOWN&STORE KWANGYA', 'SM TOWN&STORE KWANGYA Seoul', 'SM 아티스트 굿즈와 카페가 함께 있는 복합 문화 공간으로 광야라는 메타버스 세계관을 현실에 구현한 혁신적인 공간입니다. SM 소속 아티스트들의 독점 굿즈와 한정판 상품을 만날 수 있으며, 팬들을 위한 특별한 경험을 제공합니다.', 'A complex cultural space combining SM artist goods and a cafe, innovatively implementing the metaverse worldview of KWANGYA in reality. Visitors can discover exclusive merchandise and limited edition products from SM artists while enjoying special experiences designed for fans.', 127.0276, 37.5654, 'https://example.com/smtown_kwangya.jpg', '서울특별시 강남구 압구정로', '02-6002-8888', 'https://www.smtownstore.com', 'smtown_kwangya_kakao_008', 'smtown_kwangya_tour_008', '{"monday": "10:00-21:00", "tuesday": "10:00-21:00", "wednesday": "10:00-21:00", "thursday": "10:00-21:00", "friday": "10:00-22:00", "saturday": "10:00-22:00", "sunday": "10:00-21:00"}', '["K_POP"]', '무료 입장, 카페 이용 시 별도 비용', NOW(), NOW()),
    (1, 'AK PLAZA 홍대점', 'AK Plaza Hongdae', '홍대의 젊음과 활기를 대표하는 쇼핑몰로 다양한 케이팝 관련 매장들이 입점해 있는 복합 쇼핑 공간입니다. 패션부터 뷰티, K-pop 굿즈까지 한 건물에서 모든 것을 해결할 수 있어 젊은 층과 관광객들에게 인기가 높습니다.', 'A shopping mall representing the youth and vitality of Hongdae, featuring a complex shopping space with various K-pop related stores. Popular among young people and tourists as everything from fashion to beauty and K-pop goods can be found in one building.', 126.9201, 37.5568, 'https://example.com/ak_plaza.jpg', '서울특별시 마포구 양화로', '02-330-1114', 'https://www.akplaza.com', 'ak_plaza_kakao_009', 'ak_plaza_tour_009', '{"monday": "10:30-22:00", "tuesday": "10:30-22:00", "wednesday": "10:30-22:00", "thursday": "10:30-22:00", "friday": "10:30-22:30", "saturday": "10:30-22:30", "sunday": "10:30-22:00"}', '["K_POP"]', '무료 입장', NOW(), NOW()),
    (1, '월드케이팝센터', 'World K-pop Center', '원데이 케이팝 댄스 클래스를 제공하는 전문 센터로 K-pop 문화를 직접 체험할 수 있는 특별한 공간입니다. 전문 강사진과 함께 인기 아이돌의 안무를 배우며 K-pop 스타가 된 듯한 경험을 할 수 있어 외국인 관광객들에게 특히 인기가 높습니다.', 'A professional center offering one-day K-pop dance classes, providing a special space to directly experience K-pop culture. Particularly popular among foreign tourists, visitors can learn choreography from popular idols with professional instructors and experience being a K-pop star.', 126.9785, 37.5710, 'https://example.com/world_kpop.jpg', '서울특별시 종로구 세종대로', '02-123-4567', 'https://www.worldkpopcenter.com', 'world_kpop_kakao_010', 'world_kpop_tour_010', '{"monday": "09:00-21:00", "tuesday": "09:00-21:00", "wednesday": "09:00-21:00", "thursday": "09:00-21:00", "friday": "09:00-21:00", "saturday": "09:00-18:00", "sunday": "10:00-18:00"}', '["K_POP"]', '체험 클래스 30,000원~50,000원', NOW(), NOW()),
    (1, '리얼케이팝댄스', 'Real K-pop Dance', '케이팝 댄스를 배우는 체험 프로그램을 전문으로 하는 댄스 스튜디오입니다. 초보자부터 고급자까지 수준별 맞춤 수업을 제공하며, 실제 K-pop 안무가들이 직접 지도하여 정통 케이팝 댄스를 배울 수 있는 곳입니다.', 'A dance studio specializing in K-pop dance learning experience programs. Offering customized classes from beginner to advanced levels, visitors can learn authentic K-pop dance directly from actual K-pop choreographers.', 126.9289, 37.5547, 'https://example.com/real_kpop_dance.jpg', '서울특별시 마포구 홍대입구', '02-987-6543', 'https://www.realkpopdance.com', 'real_kpop_kakao_011', 'real_kpop_tour_011', '{"monday": "10:00-22:00", "tuesday": "10:00-22:00", "wednesday": "10:00-22:00", "thursday": "10:00-22:00", "friday": "10:00-22:00", "saturday": "09:00-22:00", "sunday": "10:00-20:00"}', '["K_POP"]', '1회 수업 25,000원~40,000원', NOW(), NOW()),
    (1, '신촌/홍대 버스킹 거리', 'Sinchon/Hongdae Busking Street', '젊음의 거리 홍대와 신촌을 잇는 버스킹 공연의 메카로 다양한 장르의 거리 공연을 즐길 수 있는 곳입니다. K-pop 커버 댄스부터 라이브 공연까지 다채로운 볼거리가 가득하며, 누구나 자유롭게 공연을 관람할 수 있는 열린 문화 공간입니다.', 'A mecca of busking performances connecting the youthful districts of Hongdae and Sinchon, where visitors can enjoy street performances of various genres. From K-pop cover dances to live performances, it offers diverse entertainment and serves as an open cultural space where anyone can freely watch performances.', 126.9342, 37.5560, 'https://example.com/busking_street.jpg', '서울특별시 마포구 홍대입구역 일대', NULL, NULL, 'busking_kakao_012', 'busking_tour_012', '{"monday": "상시 개방", "tuesday": "상시 개방", "wednesday": "상시 개방", "thursday": "상시 개방", "friday": "상시 개방", "saturday": "상시 개방", "sunday": "상시 개방"}', '["K_POP"]', '무료 관람', NOW(), NOW()),
    (1, '더키월드', 'The Key World', '케이팝과 관련된 다양한 체험과 쇼핑을 한 번에 즐길 수 있는 복합 엔터테인먼트 공간입니다. VR 체험부터 포토존, 굿즈 쇼핑까지 K-pop 팬들이 꿈꿔온 모든 것을 경험할 수 있으며, 특히 인터랙티브한 체험 프로그램들로 유명합니다.', 'A complex entertainment space where visitors can enjoy various K-pop related experiences and shopping all at once. From VR experiences to photo zones and goods shopping, fans can experience everything they''ve dreamed of, particularly famous for its interactive experience programs.', 126.9756, 37.5658, 'https://example.com/the_key_world.jpg', '서울특별시 중구 을지로', '02-2222-3333', 'https://www.thekeyworld.com', 'thekey_kakao_013', 'thekey_tour_013', '{"monday": "10:00-21:00", "tuesday": "10:00-21:00", "wednesday": "10:00-21:00", "thursday": "10:00-21:00", "friday": "10:00-22:00", "saturday": "10:00-22:00", "sunday": "10:00-21:00"}', '["K_POP"]', '입장료 10,000원, 체험비 별도', NOW(), NOW());
INSERT INTO places (region_id, name_ko, name_en, description_ko, description_en, longitude, latitude, image_url, address, contact_info, website_url, kakao_place_id, tour_api_place_id, opening_hours, themes, cost_info, created_at, updated_at)
VALUES
    (2, '아홉산 숲', 'Ahopsan Forest', '부산 기장군에 위치한 아름다운 자연 숲으로 다양한 드라마와 영화의 촬영지로 사랑받고 있는 명소입니다. 울창한 숲과 맑은 공기, 그리고 드라마 속 로맨틱한 장면들이 촬영된 곳으로 연인들과 가족 단위 방문객들에게 인기가 높습니다.', 'A beautiful natural forest located in Gijang-gun, Busan, beloved as a filming location for various dramas and movies. Popular among couples and families for its dense forest, fresh air, and romantic scenes from dramas filmed here.', 129.0756, 35.1796, 'https://example.com/ahopsan.jpg', '부산광역시 기장군 철마면', '051-709-4000', NULL, 'ahopsan_kakao_014', 'ahopsan_tour_014', '{"monday": "상시 개방", "tuesday": "상시 개방", "wednesday": "상시 개방", "thursday": "상시 개방", "friday": "상시 개방", "saturday": "상시 개방", "sunday": "상시 개방"}', '["K_DRAMA"]', '무료 입장', NOW(), NOW()),
    (2, '부산시민공원', 'Busan Citizens Park', '부산의 대표적인 도심 공원으로 시민들의 휴식 공간이자 여러 드라마의 촬영지로 활용되고 있는 곳입니다. 넓은 잔디밭과 아름다운 산책로, 그리고 다양한 문화 시설들이 어우러져 있어 관광객들에게도 인기가 높은 명소입니다.', 'Busan''s representative urban park serving as a resting place for citizens and utilized as a filming location for various dramas. Popular among tourists for its vast lawns, beautiful walking trails, and diverse cultural facilities harmoniously integrated.', 129.0588, 35.1677, 'https://example.com/citizens_park.jpg', '부산광역시 부산진구 시민공원로', '051-850-6000', 'https://www.bscitizenspark.or.kr', 'citizens_park_kakao_015', 'citizens_park_tour_015', '{"monday": "05:00-23:00", "tuesday": "05:00-23:00", "wednesday": "05:00-23:00", "thursday": "05:00-23:00", "friday": "05:00-23:00", "saturday": "05:00-23:00", "sunday": "05:00-23:00"}', '["K_DRAMA"]', '무료 입장', NOW(), NOW()),
    (2, '다대포해수욕장', 'Dadaepo Beach', '부산의 서쪽 끝에 위치한 아름다운 해수욕장으로 낙조가 특히 유명한 드라마 촬영의 단골 장소입니다. 넓은 백사장과 완만한 경사로 가족 단위 방문객들이 많으며, 드라마 속 감동적인 장면들이 많이 촬영된 로맨틱한 명소입니다.', 'A beautiful beach located at the western end of Busan, particularly famous for its sunsets and as a regular filming location for dramas. With its wide sandy beach and gentle slope, it attracts many family visitors and serves as a romantic spot where many touching drama scenes have been filmed.', 128.9688, 35.0465, 'https://example.com/dadaepo.jpg', '부산광역시 사하구 다대포해변로', '051-220-4000', 'https://www.bto.or.kr/dadaepo', 'dadaepo_kakao_016', 'dadaepo_tour_016', '{"monday": "상시 개방", "tuesday": "상시 개방", "wednesday": "상시 개방", "thursday": "상시 개방", "friday": "상시 개방", "saturday": "상시 개방", "sunday": "상시 개방"}', '["K_DRAMA"]', '무료 입장', NOW(), NOW()),
    (2, '동백섬', 'Dongbaek Island', '해운대와 연결된 작은 섬으로 아름다운 해안 경관과 함께 수많은 드라마의 배경이 된 부산의 대표 관광지입니다. 동백나무 숲과 해안 산책로, 그리고 누리마루 APEC하우스가 있어 문화와 자연이 조화를 이루는 특별한 공간입니다.', 'A small island connected to Haeundae, serving as Busan''s representative tourist destination with beautiful coastal scenery and the backdrop for numerous dramas. A special space where culture and nature harmonize, featuring camellia forests, coastal walking trails, and Nurimaru APEC House.', 129.1586, 35.1540, 'https://example.com/dongbaek.jpg', '부산광역시 해운대구 동백로', '051-749-4000', NULL, 'dongbaek_kakao_017', 'dongbaek_tour_017', '{"monday": "상시 개방", "tuesday": "상시 개방", "wednesday": "상시 개방", "thursday": "상시 개방", "friday": "상시 개방", "saturday": "상시 개방", "sunday": "상시 개방"}', '["K_DRAMA"]', '무료 입장', NOW(), NOW()),
    (2, '감천문화마을', 'Gamcheon Culture Village', '부산의 마추픽추라 불리는 계단식 마을로 알록달록한 집들이 어우러진 독특한 경관으로 유명한 드라마 촬영지입니다. 좁은 골목길과 벽화, 그리고 바다가 내려다보이는 전망으로 국내외 관광객들이 꼭 찾는 부산의 대표 명소입니다.', 'Known as Busan''s Machu Picchu, this terraced village is famous as a drama filming location with its unique landscape of colorful houses. A representative Busan attraction that domestic and international tourists must visit, featuring narrow alleys, murals, and ocean views.', 129.0104, 35.0976, 'https://example.com/gamcheon.jpg', '부산광역시 사하구 감내2로', '051-204-1444', 'http://www.gamcheon.or.kr', 'gamcheon_kakao_018', 'gamcheon_tour_018', '{"monday": "09:00-18:00", "tuesday": "09:00-18:00", "wednesday": "09:00-18:00", "thursday": "09:00-18:00", "friday": "09:00-18:00", "saturday": "09:00-18:00", "sunday": "09:00-18:00"}', '["K_DRAMA"]', '무료 입장 (일부 체험 프로그램 유료)', NOW(), NOW()),
    (2, '광안리해수욕장/광안대교', 'Gwangalli Beach/Gwangan Bridge', '부산의 대표적인 해수욕장과 랜드마크인 광안대교가 어우러진 아름다운 야경으로 유명한 드라마 촬영의 단골 장소입니다. 특히 밤에는 광안대교의 조명과 함께 환상적인 경관을 자랑하며, 로맨틱한 드라마 장면의 배경으로 자주 등장합니다.', 'A regular drama filming location famous for its beautiful night view combining Busan''s representative beach and landmark Gwangan Bridge. Particularly at night, it boasts fantastic scenery with the bridge''s illumination and frequently appears as the backdrop for romantic drama scenes.', 129.1185, 35.1532, 'https://example.com/gwangalli.jpg', '부산광역시 수영구 광안해변로', '051-610-4000', NULL, 'gwangalli_kakao_019', 'gwangalli_tour_019', '{"monday": "상시 개방", "tuesday": "상시 개방", "wednesday": "상시 개방", "thursday": "상시 개방", "friday": "상시 개방", "saturday": "상시 개방", "sunday": "상시 개방"}', '["K_DRAMA"]', '무료 입장', NOW(), NOW()),
    (2, '흰여울문화마을', 'Huinnyeoul Culture Village', '절벽 위에 자리한 하얀 집들이 인상적인 문화마을로 바다와 어우러진 아름다운 풍경으로 드라마 촬영지로 인기가 높은 곳입니다. 좁은 골목길을 따라 펼쳐지는 카페와 갤러리들, 그리고 탁 트인 바다 전망이 방문객들에게 특별한 경험을 선사합니다.', 'A culture village with impressive white houses perched on cliffs, popular as a drama filming location for its beautiful scenery harmonizing with the sea. Cafes and galleries spread along narrow alleys, along with open ocean views, provide visitors with special experiences.', 129.0754, 35.0513, 'https://example.com/huinnyeoul.jpg', '부산광역시 영도구 흰여울길', '051-419-4000', NULL, 'huinnyeoul_kakao_020', 'huinnyeoul_tour_020', '{"monday": "상시 개방", "tuesday": "상시 개방", "wednesday": "상시 개방", "thursday": "상시 개방", "friday": "상시 개방", "saturday": "상시 개방", "sunday": "상시 개방"}', '["K_DRAMA"]', '무료 입장', NOW(), NOW()),
    (2, '태종대', 'Taejongdae', '부산 남단의 대표적인 관광명소로 기암괴석과 울창한 숲, 그리고 푸른 바다가 어우러진 절경으로 유명한 드라마 촬영지입니다. 자연이 만든 아름다운 해안선과 등대, 전망대에서 바라보는 탁 트인 바다 풍경이 많은 드라마에서 감동적인 배경으로 사용됩니다.', 'A representative tourist attraction in southern Busan, famous as a drama filming location for its magnificent scenery combining strange rock formations, dense forests, and blue sea. The beautiful coastline created by nature, lighthouse, and open sea views from the observatory are used as moving backdrops in many dramas.', 129.0869, 35.0513, 'https://example.com/taejongdae.jpg', '부산광역시 영도구 태종로', '051-405-2004', 'http://www.taejongdae.or.kr', 'taejongdae_kakao_021', 'taejongdae_tour_021', '{"monday": "05:00-24:00", "tuesday": "05:00-24:00", "wednesday": "05:00-24:00", "thursday": "05:00-24:00", "friday": "05:00-24:00", "saturday": "05:00-24:00", "sunday": "05:00-24:00"}', '["K_DRAMA"]', '무료 입장 (관광열차 이용 시 별도 요금)', NOW(), NOW()),
    (2, '도모헌', 'Domoheon', '부산의 전통 한옥 문화공간으로 고즈넉한 분위기와 아름다운 정원으로 드라마 촬영지로 사랑받고 있는 곳입니다. 전통 차 문화 체험과 함께 한국의 미를 느낄 수 있으며, 드라마 속 한국 전통 문화를 보여주는 배경으로 자주 등장합니다.', 'A traditional hanok cultural space in Busan, beloved as a drama filming location for its serene atmosphere and beautiful garden. Visitors can experience traditional tea culture and feel Korean beauty, frequently appearing as a backdrop showcasing Korean traditional culture in dramas.', 129.0345, 35.1087, 'https://example.com/domoheon.jpg', '부산광역시 동구 망양로', '051-467-8081', 'http://www.domoheon.co.kr', 'domoheon_kakao_022', 'domoheon_tour_022', '{"monday": "10:00-18:00", "tuesday": "10:00-18:00", "wednesday": "10:00-18:00", "thursday": "10:00-18:00", "friday": "10:00-18:00", "saturday": "10:00-18:00", "sunday": "closed"}', '["K_DRAMA"]', '입장료 3,000원, 차 체험 별도', NOW(), NOW()),
    (2, '영화의 전당', 'Busan Cinema Center', '부산국제영화제의 메인 회장으로 사용되는 현대적인 건축물로 영화와 드라마 관련 이벤트가 자주 열리는 문화 공간입니다. 독특한 지붕 디자인과 LED 조명으로 유명하며, 한국 영상 문화의 중심지로서 드라마에서도 중요한 배경으로 등장합니다.', 'A modern architectural building used as the main venue for the Busan International Film Festival, serving as a cultural space where movie and drama-related events are frequently held. Famous for its unique roof design and LED lighting, it appears as an important backdrop in dramas as the center of Korean visual culture.', 129.1296, 35.1687, 'https://example.com/cinema_center.jpg', '부산광역시 해운대구 수영강변대로', '051-780-6000', 'http://www.bisco.or.kr', 'cinema_center_kakao_023', 'cinema_center_tour_023', '{"monday": "09:00-22:00", "tuesday": "09:00-22:00", "wednesday": "09:00-22:00", "thursday": "09:00-22:00", "friday": "09:00-22:00", "saturday": "09:00-22:00", "sunday": "09:00-22:00"}', '["K_DRAMA"]', '무료 입장 (공연 관람 시 별도 요금)', NOW(), NOW()),
    (2, '송정 구덕포길', 'Songjeong Gudeokpo-gil', '송정해수욕장 인근의 아름다운 해안 산책로로 드라마 촬영지로 인기가 높은 곳입니다. 바다를 바라보며 걸을 수 있는 낭만적인 길로, 특히 일출과 일몰 시간대의 아름다운 풍경으로 많은 드라마에서 감성적인 장면의 배경으로 활용됩니다.', 'A beautiful coastal walking trail near Songjeong Beach, popular as a drama filming location. A romantic path where visitors can walk while looking at the sea, particularly utilized as the backdrop for emotional scenes in many dramas for its beautiful sunrise and sunset views.', 129.1998, 35.1789, 'https://example.com/songjeong.jpg', '부산광역시 해운대구 송정해변로', '051-749-7614', NULL, 'songjeong_kakao_024', 'songjeong_tour_024', '{"monday": "상시 개방", "tuesday": "상시 개방", "wednesday": "상시 개방", "thursday": "상시 개방", "friday": "상시 개방", "saturday": "상시 개방", "sunday": "상시 개방"}', '["K_DRAMA"]', '무료 입장', NOW(), NOW()),
    (2, '문화공감수정', 'Culture Gonggam Sujeong', '부산의 문화예술 공간으로 다양한 전시와 공연이 열리며 드라마 촬영지로도 활용되고 있는 복합 문화 시설입니다. 현대적인 건축과 예술적 분위기가 어우러져 있어 문화 관련 드라마나 예술을 다룬 작품들의 배경으로 자주 등장합니다.', 'A complex cultural facility in Busan serving as a cultural arts space where various exhibitions and performances are held, also utilized as a drama filming location. Combining modern architecture with an artistic atmosphere, it frequently appears as the backdrop for culture-related dramas and art-themed works.', 129.0987, 35.1456, 'https://example.com/gonggam_sujeong.jpg', '부산광역시 동구 중앙대로', '051-640-4000', 'http://www.bsac.or.kr', 'gonggam_kakao_025', 'gonggam_tour_025', '{"monday": "09:00-22:00", "tuesday": "09:00-22:00", "wednesday": "09:00-22:00", "thursday": "09:00-22:00", "friday": "09:00-22:00", "saturday": "09:00-18:00", "sunday": "09:00-18:00"}', '["K_DRAMA"]', '무료 입장 (특별전시 유료)', NOW(), NOW());
